import React, { Component } from 'react';
import { Container,Card } from 'reactstrap';
import { Media,CardBody,CardImg,CardTitle,CardText,CardSubtitle } from 'reactstrap';
import axios from 'axios';

class Profile extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLoaded:false,
            reviews:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:5000/api/profile/posts').then((res)=>{
            this.setState({
                isLoaded:true, 
                reviews: res.data
            })
        })
    }
    render(){
        return (
            <div style={{margin:"150px",}}>
            <Container>
                <Card>  
                <Media>
                    <img src={this.props.user.avatar}
                        style={{borderRadius:"50%",padding:"50px"}} height='20%' width="20%"
                        alt="Avatar" />
                    <div style={{padding:"50px"}}>
                    <h1>{this.props.user.username}</h1>
                    <p className='ml-5 mt-1'>bio</p>
                    </div>
                </Media>
                <div style={{padding:"50px"}}>
                    <h3>Reviews</h3>         
                    <CardImg top width="100%" alt="Poster" />
                    <CardBody>
                        <CardTitle tag="h5">{this.props.user.reviews.movie}</CardTitle>
                        <CardSubtitle tag="h6" className="mb-2 text-muted">Details</CardSubtitle>
                        <CardText>{this.props.user.reviews.body}</CardText>
                        <CardText>{this.props.user.reviews.stars}</CardText>
                    </CardBody>
                </div>
                </Card>
            </Container>
            </div>
        );
    };
}

export default Profile;
