import React, { Component,Fragment } from 'react';
import { Container,Card } from 'reactstrap';
import { Media,CardBody,CardImg,CardTitle,CardText,CardSubtitle } from 'reactstrap';
import axios from 'axios';

//import Post from '../src/Post'

const Review =({reviews}) =>{
    return reviews.map(review =>{
      return (
      <Container>
         <div style={{display:"grid", gridTemplateColumns:"50% 50%", padding:"10px",textAlign:"left"}}>
             <h5>{review.title}</h5>
             <h6>{review.body}</h6>
             <h7>{review.stars} stars</h7>
                    
        </div>
      </Container>
      )
            
          
     
      
    })
}

class Profile extends Component {
    constructor(props){
        super(props);
        this.state = {
            reviews:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:5000/api/posts/user'+this.props.user.username.slice(1)).then((res)=>{
            this.setState({ 
                reviews: res.data
            })
        })
    }
    render(){
        return (
            <div style={{margin:"150px",}}>
            <Container>
                <Card>  
                <Media>
                    <img src={this.props.user.avatar}
                        style={{borderRadius:"50%",padding:"50px"}} height='20%' width="20%"
                        alt="Avatar" />
                    <div style={{padding:"50px"}}>
                    <h1>{this.props.user.username}</h1>
                    <p className='ml-5 mt-1'>bio</p>
                    </div>
                </Media>
                <div style={{padding:"50px"}}>
                <h3>Reviews</h3>         
                    
                    <CardBody>
                        <Review reviews= {this.props.user.reviews}/>
                        
                        
                        
                        
                    </CardBody>
                </div>
                </Card>
            </Container>
            </div>
        );
    };
}

export default Profile;
